from app_01.ext import db# 先有班级，后有学生class Grade(db.Model):    id = db.Column(db.Integer, autoincrement=True, primary_key=True)    g_name = db.Column(db.String(20))    # 班级的学生    # db.relationship('Student',backref='grade(表名)',lazy=True)    g_students = db.relationship('Student', backref='grade', lazy=True)class Student(db.Model):    id = db.Column(db.Integer, primary_key=True, autoincrement=True)    s_name = db.Column(db.String(20), nullable=False)    # 添加逻辑删除    isdelete = db.Column(db.Boolean, default=False)    # 添加外间    s_grade = db.Column(db.Integer, db.ForeignKey(Grade.id))