from flask import Flaskfrom app_02.ext import db, init_extfrom app_02.settings import configfrom app_02.views import blue, init_bluedef create_app(env_name=None):    # 创建app对象    app = Flask(__name__,static_folder='static')    # 先配置数据库环境 第二步    app.config.from_object(config.get(env_name or 'default'))    # 数据库初始化    db.init_app(app)    # ext 的导入    init_ext(app)    # 蓝图初始化    init_blue(app)    return app