from flask import Blueprint, render_template, jsonifyfrom app_03.models import Student# 创建蓝图对象blue = Blueprint('blue', __name__)# app注册蓝图def init_blue(app):    app.register_blueprint(blueprint=blue)@blue.route('/')def fruits():    return render_template('fruits.html')@blue.route('/fruits')def hello_world():    fruits = {        'name': 'apple',        'price': '12.5元',        'category': '洛川苹果',    }    return jsonify(fruits)